import React, { useEffect, useState } from "react";
import NewsCard from "./components/NewsCard";
import NewsDetails from "./components/NewsDetails";
import SkeletonCard from "./components/SkeletonCard";
import axios from "axios";

const API_BASE_URL = "http://127.0.0.1:8000";

function App() {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedArticle, setSelectedArticle] = useState(null);
  const [detailsLoading, setDetailsLoading] = useState(false);

  const fetchArticles = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/api/articles`);
      setArticles(response.data);
    } catch (error) {
      console.error("Error fetching articles:", error);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchArticles();
  }, []);

  const handleArticleClick = async (article) => {
    setSelectedArticle(article);
    setDetailsLoading(false);
  };

  return (
    <div className="min-h-screen bg-black text-white p-6 flex justify-center">
      <div className="w-[90%] max-w-7xl flex gap-6">
        {/* LEFT: Articles List */}
        <div className="w-2/5 bg-gray-900 p-6 rounded-xl shadow-lg">
          <div className="flex justify-between items-center mb-4">
            <h1 className="text-2xl font-bold text-center">News Articles</h1>
          </div>

          <div className="grid gap-4">
            {loading
              ? [...Array(5)].map((_, index) => <SkeletonCard key={index} />)
              : articles.map((article) => (
                  <NewsCard
                    key={article.id}
                    topic={article.title}
                    summary={article.summary}
                    onClick={() => handleArticleClick(article)}
                    selected={selectedArticle?.id === article.id}
                  />
                ))}
          </div>
        </div>

        {/* RIGHT: Article Details */}
        <div className="w-3/5 bg-gray-900 p-6 rounded-xl shadow-lg">
          <h1 className="text-2xl font-bold text-center">Article Details</h1>
          <br />

          {detailsLoading ? (
            <SkeletonCard />
          ) : selectedArticle ? (
            <NewsDetails
              title={selectedArticle.title}
              summary={selectedArticle.summary}
              url={selectedArticle.url}
              source={selectedArticle.source}
              onClose={() => setSelectedArticle(null)}
            />
          ) : (
            <p className="text-gray-400 text-center">
              Select an article to see details here.
            </p>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
