import { motion } from "framer-motion";

const NewsCard = ({ topic, summary, onClick }) => {
  return (
    <motion.div
      className="bg-gray-800 text-white p-4 rounded-xl shadow-lg hover:bg-gray-700 transition cursor-pointer"
      whileHover={{ scale: 1.05 }}
      onClick={onClick}
    >
      <h2 className="text-xl font-bold">{topic}</h2>
      <p className="text-sm mt-2">{summary}</p>
    </motion.div>
  );
};

export default NewsCard;
