import React, { useEffect, useState } from "react";
import NewsCard from "./components/NewsCard";
import NewsDetails from "./components/NewsDetails";
import SkeletonCard from "./components/SkeletonCard";

function App() {
  const [topics, setTopics] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedTopic, setSelectedTopic] = useState(null);

useEffect(() => {
  setLoading(true);  // Start in loading mode

  fetch("http://127.0.0.1:8000/news")
    .then(response => response.json())
    .then(data => {
      setTimeout(() => {  // Force a delay in UI
        setTopics(data);
        setLoading(false);
      }, 5000);  // 5 seconds delay
    })
    .catch(error => {
      console.error("Error fetching news:", error);
      setLoading(false);
    });
}, []);

  return (
    <div className="min-h-screen bg-black text-white p-6">
      <h1 className="text-3xl font-bold mb-4">News Summarizer</h1>
      {/* News Cards or Skeleton Loader */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {loading
          ? [...Array(4)].map((_, index) => <SkeletonCard key={index} />) // Show skeletons
          : topics.map((topic) => (
              <NewsCard
                key={topic.name}
                topic={topic.name}
                summary={topic.summary}
                onClick={() => setSelectedTopic(topic)}
              />
            ))}
      </div>

      {/* News Details Modal */}
      {selectedTopic && (
        <div className="fixed inset-0 flex justify-center items-center bg-black bg-opacity-80">
          <NewsDetails {...selectedTopic} onClose={() => setSelectedTopic(null)} />
        </div>
      )}
    </div>
  );
}

export default App;
