    <div className="bg-gray-800 p-6 rounded-lg shadow-md">
      <h2 className="text-2xl font-bold">{title}</h2>
      <p className="text-gray-400 mt-2">{key}</p>

      <h3 className="text-lg font-semibold mt-4">Insights:</h3>
