const NewsDetails = ({ key, title, summary, insights, articles, onClose }) => {
  return (
    <div className="bg-gray-800 p-6 rounded-lg shadow-md">
      <h2 className="text-2xl font-bold">{title}</h2>
      <p className="text-gray-400 mt-2">{summary}</p>
      <p className="text-gray-400 mt-2">{key}</p>

      <h3 className="text-lg font-semibold mt-4">Insights:</h3>
{/*       <ul className="list-disc list-inside text-gray-400"> */}
{/*         {insights.map((insight, index) => ( */}
{/*           <li key={index}>{insight}</li> */}
{/*         ))} */}
{/*       </ul> */}

<h3 className="text-lg font-semibold mt-4">Articles:</h3>
<div className="border border-gray-700 rounded-lg p-4 bg-gray-900 shadow-md">
  {articles.map((article, index) => (
    <a
      key={index}
      href={article.url}
      target="_blank"
      rel="noopener noreferrer"
      className="block text-blue-400 hover:underline mb-2 break-words"
    >
      {article}
    </a>
  ))}
</div>

      <button onClick={onClose} className="mt-4 px-4 py-2 bg-blue-900 text-white rounded-lg">
        Close
      </button>
    </div>
  );
};

export default NewsDetails;
