import React from "react";

const NewsDetails = ({ topic, insights, articles, onClose }) => {
  return (
    <div className="bg-gray-900 text-white p-6 rounded-lg shadow-lg">
      <h1 className="text-2xl font-bold">{topic}</h1>
      <ul className="mt-4">
        {insights.map((insight, index) => (
          <li key={index} className="border-b border-gray-700 py-2">{insight}</li>
        ))}
      </ul>
      <h3 className="mt-4 text-xl">Full Articles</h3>
      {articles.map((article, index) => (
        <div key={index} className="mt-2">
          <a href={article.url} target="_blank" rel="noopener noreferrer" className="text-blue-400 underline">
            {article.url}
          </a>
          <p className="mt-1 text-gray-300">{article.text}</p>
        </div>
      ))}
      <button className="mt-4 bg-red-500 px-4 py-2 rounded" onClick={onClose}>Close</button>
    </div>
  );
};

export default NewsDetails;
