const NewsCard = ({ topic, summary, onClick, selected }) => {
  return (
    <div
      onClick={onClick}
      className={`p-4 rounded-lg shadow-md cursor-pointer transition-all duration-300
        ${selected ? "border-2 border-blue-400 shadow-blue-500/50 ring-2 ring-blue-500 scale-105" : "border border-gray-700"}`}
    >
      <h2 className="text-xl font-semibold">{topic}</h2>
      <p className="text-gray-400 mt-2">{summary}</p>
    </div>
  );
};

export default NewsCard;
