import { useState } from "react";
import { FaChevronDown, FaChevronUp } from "react-icons/fa";

const NewsDetails = ({ title, summary, url, source, onClose }) => {
  const [isTextVisible, setIsTextVisible] = useState(false);

  const toggleTextVisibility = () => {
    setIsTextVisible(!isTextVisible);
  };

  return (
    <div className="bg-gray-800 p-6 rounded-lg shadow-md">
      <h3 className="text-lg font-semibold mt-4 mb-2">{title}</h3>
      
      {/* Source */}
      <p className="text-gray-400 mb-4">Source: {source}</p>

      {/* Summary Section */}
      <h3 className="text-lg font-semibold mt-4">Summary:</h3>
      <div className="text-gray-300 whitespace-pre-line">
        {summary}
      </div>

      {/* Full Text Section with collapsible functionality */}
      <h3 className="text-lg font-semibold mt-4 mb-2">Full Text:</h3>
      {/* Toggle button for showing/hiding full text */}
      <button
        className="flex items-center text-blue-500 hover:underline"
        onClick={toggleTextVisibility}
      >
        {/* Show appropriate arrow based on visibility */}
        {isTextVisible ? (
          <FaChevronUp className="mr-2" />
        ) : (
          <FaChevronDown className="mr-2" />
        )}
        {isTextVisible ? "Hide Text" : "Show Full Text"}
      </button>

      {/* Conditionally render the full text */}
      {isTextVisible && (
        <div className="text-gray-400 mt-2">
          <p>{summary}</p>
        </div>
      )}

      {/* Source Link */}
      <div className="mt-4">
        <a
          href={url}
          target="_blank"
          rel="noopener noreferrer"
          className="text-blue-400 hover:underline break-words"
        >
          Read full article
        </a>
      </div>

      {/* Close Button */}
      <button
        onClick={onClose}
        className="mt-4 px-4 py-2 bg-blue-900 text-white rounded-lg"
      >
        Close
      </button>
    </div>
  );
};

export default NewsDetails;
